<!-- jsPDF -->
<template>
  <div>
    <h3 @click="downloadPdf">文字生成PDF下载</h3>
    <h3 @click="downloadPdf2">图片生成PDF下载</h3>
    <p>pt=px*72/dpi</p>
    <p>Windows系统默认的96dpi</p>
    <p>Apple系统默认的72dpi</p>
    <h3 @click="downloadPdf3">文字与图片生成PDF</h3>
    <hr />
    <h3>中文生成PDF下载(未完成)</h3>
  </div>
</template>

<script>
import jsPDF from "jsPDF";
export default {
  components: {},

  data() {
    return {};
  },

  created() {},

  methods: {
    // 文字生成PDF
    downloadPdf() {
      // 默认a4大小，竖直方向，mm单位的PDF
      var doc = new jsPDF();

      // 添加文本‘Download PDF’
      doc.text("Download PDF!", 10, 10);
      doc.save("a4-text.pdf");
    },

    // 图片生成PDF
    downloadPdf2() {
      // 三个参数，第一个方向，第二个单位，第三个尺寸格式
      var doc = new jsPDF("landscape", "pt", [330 * 0.75, 250 * 0.75]);

      // 将图片转化为dataUrl
      var imageData =
        "data:image/jpeg;base64,";

      doc.addImage(imageData, "PNG", 0, 0, 330 * 0.75, 250 * 0.75);
      doc.save("picture.pdf");
    },

    // 文字与图片生成PDF
    downloadPdf3() {
      // 三个参数，第一个方向，第二个尺寸，第三个尺寸格式
      var doc = new jsPDF("landscape", "pt", [205, 155]);

      // 将图片转化为dataUrl
      var imageData =
        "data:image/jpeg;base64,";

      //设置字体大小
      doc.setFontSize(20);

      //10,20这两参数控制文字距离左边，与上边的距离
      doc.text("Stone", 10, 20);

      // 0, 40, 控制文字距离左边，与上边的距离
      doc.addImage(imageData, "PNG", 0, 40, 205, 115);
      doc.save("text-picture.pdf");
    }
  }
};
</script>
